def color_miner(data):
    data_list = []
    count = int(data['count'])

    for i in range(count):
        _temp = {}
        _temp['color'] = {}
        _temp['color']['name'] = data['colors'][i]['name']['value']
        _temp['color']['c'] = data['colors'][i]['cmyk']['c']
        _temp['color']['m'] = data['colors'][i]['cmyk']['m']
        _temp['color']['y'] = data['colors'][i]['cmyk']['y']
        _temp['color']['k'] = data['colors'][i]['cmyk']['k']
        data_list.append(_temp)
    return data_list


