def extract_color_names_and_cmyk(data):
    color_info_list = []

    for color_info in data['colors']:
        color_dict = {
            'name': color_info['name']['value'],
            'cmyk': {
                'c': color_info['cmyk']['c'],
                'm': color_info['cmyk']['m'],
                'y': color_info['cmyk']['y'],
                'k': color_info['cmyk']['k']
            }
        }
        color_info_list.append(color_dict)

    return color_info_list

